import { useState } from 'react';
import Banner from './components/Banner';
import Formulario from './components/Formulario';
import Time from './components/Time';
import Rodape from './components/Rodape';


function App() {

  const [times,setTimes]= useState([
    {
      nome:'LOUD',
      cor:'#57C278'
    },
    {
      nome:'Fnatic',
      cor:'#82CFFA'
    },
    {
      nome:'Paper Rex',
      cor:'#A6D157'
    },
    {
      nome:'DRX',
      cor:'#E06B69'
    },
    {
      nome:'Team Liquid',
      cor:'#DB6EBF'
    }
  ])

  const [colaboradorers, setColaboradores] = useState([])

  const aoNovoColaboradorAdicionado = (colaborador)=>{
    setColaboradores([...colaboradorers,colaborador])

  }
  function deletarColaborador(nome){
    console.log('deletar colaborador',nom)
  }

  function mudarCorDoTime(cor,nome){
    setTimes(times.map(time => {if(time.nome === nome){
      time.cor = cor
    } return time}));
  }
  return (

    <div className="App">
      <Banner />
      <Formulario times={times.map(time => time.nome)} aoColaboradorCadastrado={colaborador =>aoNovoColaboradorAdicionado(colaborador)}/>

      {times.map(time => <Time 
      mudarCor = {mudarCorDoTime}
      key={time.nome} 
      nome={time.nome} 
      corSecundaria={time.corSecundaria} 
      cor={time.cor}
      colaboradores={colaboradorers.filter(colaborador => colaborador.time === time.nome)}
      aoDeletar={deletarColaborador}
      /> )}
      <Rodape/>
    </div>


  );
}

export default App;
