import Colaborador from "../Colaborador"
import "../Time/Time.css"

export default function Time(props){
    return(
        //dumb components
        //renderisação condicional 
        //props.colaboradores.length = condição 
        //pode ser feito com operador ternario se 0 ? execulta se não : "{string vasia}"
        props.colaboradores.length > 0 && <section className="time"  style={{backgroundColor: props.corSecundaria}}>
            <input onChange={props.mudarCor(props.corPrimaria, pr)} value={props.corPrimaria} type="color" className="input-color" />
            <h3 style={{borderColor: props.corPrimaria}}>{props.nome}</h3>
            <div className="colaboradores">
            {props.colaboradores.map(colaborador => {
                return(
                    <Colaborador corDeFundo={props.corPrimaria} key={colaborador.nome} nome={colaborador.nome} cargo={colaborador.cargo} imagem={colaborador.imagem} aoDeletar={props.aoDeletar}/>
                )
            })}
            </div>
        </section>
    )
}
