import { useEffect, useState } from 'react';
import Banner from './components/Banner';
import Formulario from './components/Formulario';
import Time from './components/Time';
import Rodape from './components/Rodape';
import { v4 as uuidv4 } from 'uuid';


function App() {

  const [times,setTimes]= useState([
    {
      id : uuidv4(),
      nome:'LOUD',
      cor:'#13ff00'
    },
    {
      id : uuidv4(),
      nome:'Fnatic',
      cor:'#ff5900'
    },
    {
      id : uuidv4(),
      nome:'Paper Rex',
      cor:'#A6D157'
    },
    {
      id : uuidv4(),
      nome:'DRX',
      cor:'#0f03a3'
    },
    {
      id : uuidv4(),
      nome:'Team Liquid',
      cor:'#DB6EBF'
    }
  ])


  const [colaboradorers, setColaboradores] = useState([])

  const [colaboradorPreDef,setColaboradorPreDef] =  useState('')

  const aoNovoColaboradorAdicionado = (colaborador)=>{
    setColaboradores([...colaboradorers,colaborador])
  }
  function deletarColaborador(id){
    setColaboradores(colaboradorers.filter(colaborador => colaborador.id !== id))
  }

  function mudarCorDoTime(cor,id){
    setTimes(times.map(time => {if(time.id === id){
      time.cor = cor
    } return time}));
  }

  function cadastrarTime(novoTime) {
    setTimes([...times, { ...novoTime, id: uuidv4() } ])
  }

  function resolverFavorito(id) {
    setColaboradores(colaboradorers.map(colaborador => {
      if(colaborador.id === id) colaborador.favorito = !colaborador.favorito;
      return colaborador;
    }))
  }

  useEffect( ( ) =>{
    fetch("http://localhost:8080/LOUD")
    .then(resposta => resposta.json())
    .then(dados => setColaboradorPreDef(dados))
}, [])
  return (

    <div className="App">
      <Banner />
      <Formulario 
      cadastrarTime ={cadastrarTime}
        times={times.map(time => time.nome)}
        aoColaboradorCadastrado={colaborador =>aoNovoColaboradorAdicionado(colaborador)}
      />
      {times.map(time => <Time 
      mudarCor = {mudarCorDoTime}
      key={time.nome} 
      nome={time.nome} 
      corSecundaria={time.corSecundaria} 
      cor={time.cor}
      colaboradores={colaboradorers.filter(colaborador => colaborador.time === time.nome)}
      aoDeletar={deletarColaborador}
      aoFavoritar={resolverFavorito} 
      id={time.id}
      /> )}
      <Rodape/>
    </div>


  );
}

export default App;
